// Extract first user_id from webhook payload
const body = $input.item.json.body;
const userIds = body.target_config?.user_ids || [];
const userId = userIds[0];
const reportType = body.report_type || 'pattern_analysis';
const userName = body.user_name || 'User';

if (!userId) {
  throw new Error('No user_id provided in target_config.user_ids');
}

return {
  json: {
    user_id: userId,
    report_type: reportType,
    user_name: userName,
    triggered_by: body.triggered_by || 'manual'
  }
};

// Combine all user data for Claude
const userData = $('Extract User ID').item.json;
const profileData = $('Load User Profile').item.json;
const practicesData = $('Load Daily Practices').item.json;
const streaksData = $('Load Practice Streaks').item.json;

// Handle arrays from Supabase
const profile = Array.isArray(profileData) ? profileData[0] : profileData;
const practices = Array.isArray(practicesData) ? practicesData : [practicesData].filter(Boolean);
const streaks = Array.isArray(streaksData) ? streaksData[0] : streaksData;

// Calculate metrics
const completedPractices = practices.filter(p => p && p.completed);
const completionRate = practices.length > 0 ? completedPractices.length / practices.length : 0;
const currentStreak = streaks?.current_streak || 0;
const longestStreak = streaks?.longest_streak || 0;

return {
  json: {
    user_id: userData.user_id,
    user_name: userData.user_name || profile?.full_name || 'User',
    report_type: userData.report_type,
    profile: profile || {},
    practices: practices,
    streaks: streaks || {},
    metrics: {
      completion_rate: completionRate,
      current_streak: currentStreak,
      longest_streak: longestStreak,
      total_practices: practices.length,
      completed_practices: completedPractices.length
    }
  }
};

