// Parse Claude response and prepare for Supabase
const claudeResponse = $input.item.json;
const userData = $('Combine User Data').item.json;
let reportContent;

try {
  // Extract content from Claude API response structure
  const content = claudeResponse.content?.[0]?.text || claudeResponse.text || '';

  // Try to parse JSON - remove any markdown code blocks if present
  let cleanContent = content.trim();
  if (cleanContent.startsWith('```')) {
    cleanContent = cleanContent.replace(/^```json?\n?/, '').replace(/\n?```$/, '');
  }

  reportContent = JSON.parse(cleanContent);
} catch (e) {
  // If parsing fails, create a structured report from raw text
  const rawText = claudeResponse.content?.[0]?.text || claudeResponse.text || String(claudeResponse);
  reportContent = {
    title: `${userData.report_type.replace('_', ' ')} Report for ${userData.user_name}`,
    summary: "AI-generated behavioral analysis report.",
    sections: [{
      title: "Analysis",
      content: rawText,
      type: "insight"
    }],
    metrics: userData.metrics,
    recommendations: [],
    priority: "normal",
    confidence_score: 0.75
  };
}

return {
  json: {
    user_id: userData.user_id,
    report_type: userData.report_type,
    title: reportContent.title || `Report for ${userData.user_name}`,
    summary: reportContent.summary || 'AI-generated behavioral analysis',
    content: reportContent,
    priority: reportContent.priority || 'normal',
    confidence_score: reportContent.confidence_score || 0.8,
    source: 'n8n',
    source_workflow_id: 'MIO-Report-Generator-v4',
    display_status: 'unread',
    pinned: false
  }
};
